#!/bin/bash #clean page cache #sync #echo 1 &gt;/proc/sys/vm/drop_caches #clean dentries and inodes #sync #echo 2 &gt;/proc/sys/vm/drop_caches #clean page cache and dentries inodes, but it is not recommended in production instead use &quot;echo 1&quot; #sync #echo 3 &gt;/proc/sys/vm/drop_caches ################## # begin refresh script ################## sync if [ $(dpkg-query -W -f=&#x27;$&#123;Status&#125;&#x27; polipo 2&gt;/dev/null | grep -c &quot;ok installed&quot;) -eq 0 ]; then apt-get install polipo -y fi polipo -x echo 3 &gt;/proc/sys/vm/drop_caches swapoff -a &amp;&amp; swapon -a printf &#x27;
%s

