function rangeAlphabetic(start, stop) {
  var result = [];
  for (var idx = start.charCodeAt(0), end = stop.charCodeAt(0); idx &lt;= end; ++idx) {
    result.push(String.fromCharCode(idx));
  }
  return result;
}

let aZ = rangeAlphabetic(&#34;a&#34;, &#34;z&#34;)
  .concat(rangeAlphabetic(&#34;A&#34;, &#34;Z&#34;))
  .filter(function (el) {
    return el != null;
  }); // a-zA-Z array

