    document.addEventListener(&#34;DOMContentLoaded&#34;, newMethod);
  } else if (typeof window.attachEvent == &#34;function&#34;) {
    window.attachEvent(&#34;onload&#34;, newMethod);
  } else {
    window.onload = newMethod;
  }
}
