 &lt;h2&gt;Features:&lt;/h2&gt;&lt;ul&gt;&lt;li&gt;Detect zip file automatically&lt;/li&gt;&lt;li&gt;Extract zip to custom folder&lt;/li&gt;&lt;li&gt;Detect zip file on subfolder&lt;/li&gt;&lt;/ul&gt;&lt;img src=&#34;https://res.cloudinary.com/dimaslanjaka/image/fetch/http://www.phpshell.in/upload/images/37/unzipper-zipper.png&#34; title=&#34;PHP UNZIPPER&#34;&gt;&lt;center&gt;&lt;h2&gt;Unzipper tools php script&lt;/h2&gt;&lt;/center&gt;&lt;pre&gt;&amp;lt;?php&lt;br&gt;/**&lt;br&gt; * The Unzipper extracts .zip or .rar archives and .gz files on webservers.&lt;br&gt; * It&#39;s handy if you do not have shell access. E.g. if you want to upload a lot&lt;br&gt; * of files (php framework or image collection) as an archive to save time.&lt;br&gt; * As of version 0.1.0 it also supports creating archives.&lt;br&gt; *&lt;br&gt; * @author  Andreas Tasch, at[tec], attec.at&lt;br&gt; * @license GNU GPL v3&lt;br&gt; * @package attec.toolbox&lt;br&gt; * @version 0.1.1&lt;br&gt; */&lt;br&gt;define(&#39;VERSION&#39;, &#39;0.1.1&#39;);&lt;br&gt;&lt;br&gt;$timestart = microtime(TRUE);&lt;br&gt;$GLOBALS[&#39;status&#39;] = array();&lt;br&gt;&lt;br&gt;$unzipper = new Unzipper;&lt;br&gt;if (isset($_POST[&#39;dounzip&#39;])) {&lt;br&gt;  // Check if an archive was selected for unzipping.&lt;br&gt;  $archive = isset($_POST[&#39;zipfile&#39;]) ? strip_tags($_POST[&#39;zipfile&#39;]) : &#39;&#39;;&lt;br&gt;  $destination = isset($_POST[&#39;extpath&#39;]) ? strip_tags($_POST[&#39;extpath&#39;]) : &#39;&#39;;&lt;br&gt;  $unzipper-&amp;gt;prepareExtraction($archive, $destination);&lt;br&gt;}&lt;br&gt;&lt;br&gt;if (isset($_POST[&#39;dozip&#39;])) {&lt;br&gt;  $zippath = !empty($_POST[&#39;zippath&#39;]) ? strip_tags($_POST[&#39;zippath&#39;]) : &#39;.&#39;;&lt;br&gt;  // Resulting zipfile e.g. zipper--2016-07-23--11-55.zip.&lt;br&gt;  $zipfile = &#39;zipper-&#39; . date(&#34;Y-m-d--H-i&#34;) . &#39;.zip&#39;;&lt;br&gt;  Zipper::zipDir($zippath, $zipfile);&lt;br&gt;}&lt;br&gt;&lt;br&gt;$timeend = microtime(TRUE);&lt;br&gt;$time = round($timeend - $timestart, 4);&lt;br&gt;&lt;br&gt;/**&lt;br&gt; * Class Unzipper&lt;br&gt; */&lt;br&gt;class Unzipper {&lt;br&gt;  public $localdir = &#39;.&#39;;&lt;br&gt;  public $zipfiles = array();&lt;br&gt;&lt;br&gt;  public function __construct() {&lt;br&gt;    // Read directory and pick .zip, .rar and .gz files.&lt;br&gt;    if ($dh = opendir($this-&amp;gt;localdir)) {&lt;br&gt;      while (($file = readdir($dh)) !== FALSE) {&lt;br&gt;        if (pathinfo($file, PATHINFO_EXTENSION) === &#39;zip&#39;&lt;br&gt;          || pathinfo($file, PATHINFO_EXTENSION) === &#39;gz&#39;&lt;br&gt;          || pathinfo($file, PATHINFO_EXTENSION) === &#39;rar&#39;&lt;br&gt;        ) {&lt;br&gt;          $this-&amp;gt;zipfiles[] = $file;&lt;br&gt;        }&lt;br&gt;      }&lt;br&gt;      closedir($dh);&lt;br&gt;&lt;br&gt;      if (!empty($this-&amp;gt;zipfiles)) {&lt;br&gt;        $GLOBALS[&#39;status&#39;] = array(&#39;info&#39; =&amp;gt; &#39;.zip or .gz or .rar files found, ready for extraction&#39;);&lt;br&gt;      }&lt;br&gt;      else {&lt;br&gt;        $GLOBALS[&#39;status&#39;] = array(&#39;info&#39; =&amp;gt; &#39;No .zip or .gz or rar files found. So only zipping functionality available.&#39;);&lt;br&gt;      }&lt;br&gt;    }&lt;br&gt;  }&lt;br&gt;&lt;br&gt;  /**&lt;br&gt;   * Prepare and check zipfile for extraction.&lt;br&gt;   *&lt;br&gt;   * @param string $archive&lt;br&gt;   *   The archive name including file extension. E.g. my_archive.zip.&lt;br&gt;   * @param string $destination&lt;br&gt;   *   The relative destination path where to extract files.&lt;br&gt;   */&lt;br&gt;  public function prepareExtraction($archive, $destination = &#39;&#39;) {&lt;br&gt;    // Determine paths.&lt;br&gt;    if (empty($destination)) {&lt;br&gt;      $extpath = $this-&amp;gt;localdir;&lt;br&gt;    }&lt;br&gt;    else {&lt;br&gt;      $extpath = $this-&amp;gt;localdir . &#39;/&#39; . $destination;&lt;br&gt;      // Todo: move this to extraction function.&lt;br&gt;      if (!is_dir($extpath)) {&lt;br&gt;        mkdir($extpath);&lt;br&gt;      }&lt;br&gt;    }&lt;br&gt;    // Only local existing archives are allowed to be extracted.&lt;br&gt;    if (in_array($archive, $this-&amp;gt;zipfiles)) {&lt;br&gt;      self::extract($archive, $extpath);&lt;br&gt;    }&lt;br&gt;  }&lt;br&gt;&lt;br&gt;  /**&lt;br&gt;   * Checks file extension and calls suitable extractor functions.&lt;br&gt;   *&lt;br&gt;   * @param string $archive&lt;br&gt;   *   The archive name including file extension. E.g. my_archive.zip.&lt;br&gt;   * @param string $destination&lt;br&gt;   *   The relative destination path where to extract files.&lt;br&gt;   */&lt;br&gt;  public static function extract($archive, $destination) {&lt;br&gt;    $ext = pathinfo($archive, PATHINFO_EXTENSION);&lt;br&gt;    switch ($ext) {&lt;br&gt;      case &#39;zip&#39;:&lt;br&gt;        self::extractZipArchive($archive, $destination);&lt;br&gt;        break;&lt;br&gt;      case &#39;gz&#39;:&lt;br&gt;        self::extractGzipFile($archive, $destination);&lt;br&gt;        break;&lt;br&gt;      case &#39;rar&#39;:&lt;br&gt;        self::extractRarArchive($archive, $destination);&lt;br&gt;        break;&lt;br&gt;    }&lt;br&gt;&lt;br&gt;  }&lt;br&gt;&lt;br&gt;  /**&lt;br&gt;   * Decompress/extract a zip archive using ZipArchive.&lt;br&gt;   *&lt;br&gt;   * @param $archive&lt;br&gt;   * @param $destination&lt;br&gt;   */&lt;br&gt;  public static function extractZipArchive($archive, $destination) {&lt;br&gt;    // Check if webserver supports unzipping.&lt;br&gt;    if (!class_exists(&#39;ZipArchive&#39;)) {&lt;br&gt;      $GLOBALS[&#39;status&#39;] = array(&#39;error&#39; =&amp;gt; &#39;Error: Your PHP version does not support unzip functionality.&#39;);&lt;br&gt;      return;&lt;br&gt;    }&lt;br&gt;&lt;br&gt;    $zip = new ZipArchive;&lt;br&gt;&lt;br&gt;    // Check if archive is readable.&lt;br&gt;    if ($zip-&amp;gt;open($archive) === TRUE) {&lt;br&gt;      // Check if destination is writable&lt;br&gt;      if (is_writeable($destination . &#39;/&#39;)) {&lt;br&gt;        $zip-&amp;gt;extractTo($destination);&lt;br&gt;        $zip-&amp;gt;close();&lt;br&gt;        $GLOBALS[&#39;status&#39;] = array(&#39;success&#39; =&amp;gt; &#39;Files unzipped successfully&#39;);&lt;br&gt;      }&lt;br&gt;      else {&lt;br&gt;        $GLOBALS[&#39;status&#39;] = array(&#39;error&#39; =&amp;gt; &#39;Error: Directory not writeable by webserver.&#39;);&lt;br&gt;      }&lt;br&gt;    }&lt;br&gt;    else {&lt;br&gt;      $GLOBALS[&#39;status&#39;] = array(&#39;error&#39; =&amp;gt; &#39;Error: Cannot read .zip archive.&#39;);&lt;br&gt;    }&lt;br&gt;  }&lt;br&gt;&lt;br&gt;  /**&lt;br&gt;   * Decompress a .gz File.&lt;br&gt;   *&lt;br&gt;   * @param string $archive&lt;br&gt;   *   The archive name including file extension. E.g. my_archive.zip.&lt;br&gt;   * @param string $destination&lt;br&gt;   *   The relative destination path where to extract files.&lt;br&gt;   */&lt;br&gt;  public static function extractGzipFile($archive, $destination) {&lt;br&gt;    // Check if zlib is enabled&lt;br&gt;    if (!function_exists(&#39;gzopen&#39;)) {&lt;br&gt;      $GLOBALS[&#39;status&#39;] = array(&#39;error&#39; =&amp;gt; &#39;Error: Your PHP has no zlib support enabled.&#39;);&lt;br&gt;      return;&lt;br&gt;    }&lt;br&gt;&lt;br&gt;    $filename = pathinfo($archive, PATHINFO_FILENAME);&lt;br&gt;    $gzipped = gzopen($archive, &#34;rb&#34;);&lt;br&gt;    $file = fopen($destination . &#39;/&#39; . $filename, &#34;w&#34;);&lt;br&gt;&lt;br&gt;    while ($string = gzread($gzipped, 4096)) {&lt;br&gt;      fwrite($file, $string, strlen($string));&lt;br&gt;    }&lt;br&gt;    gzclose($gzipped);&lt;br&gt;    fclose($file);&lt;br&gt;&lt;br&gt;    // Check if file was extracted.&lt;br&gt;    if (file_exists($destination . &#39;/&#39; . $filename)) {&lt;br&gt;      $GLOBALS[&#39;status&#39;] = array(&#39;success&#39; =&amp;gt; &#39;File unzipped successfully.&#39;);&lt;br&gt;&lt;br&gt;      // If we had a tar.gz file, let&#39;s extract that tar file.&lt;br&gt;      if (pathinfo($destination . &#39;/&#39; . $filename, PATHINFO_EXTENSION) == &#39;tar&#39;) {&lt;br&gt;        $phar = new PharData($destination . &#39;/&#39; . $filename);&lt;br&gt;        if ($phar-&amp;gt;extractTo($destination)) {&lt;br&gt;          $GLOBALS[&#39;status&#39;] = array(&#39;success&#39; =&amp;gt; &#39;Extracted tar.gz archive successfully.&#39;);&lt;br&gt;          // Delete .tar.&lt;br&gt;          unlink($destination . &#39;/&#39; . $filename);&lt;br&gt;        }&lt;br&gt;      }&lt;br&gt;    }&lt;br&gt;    else {&lt;br&gt;      $GLOBALS[&#39;status&#39;] = array(&#39;error&#39; =&amp;gt; &#39;Error unzipping file.&#39;);&lt;br&gt;    }&lt;br&gt;&lt;br&gt;  }&lt;br&gt;&lt;br&gt;  /**&lt;br&gt;   * Decompress/extract a Rar archive using RarArchive.&lt;br&gt;   *&lt;br&gt;   * @param string $archive&lt;br&gt;   *   The archive name including file extension. E.g. my_archive.zip.&lt;br&gt;   * @param string $destination&lt;br&gt;   *   The relative destination path where to extract files.&lt;br&gt;   */&lt;br&gt;  public static function extractRarArchive($archive, $destination) {&lt;br&gt;    // Check if webserver supports unzipping.&lt;br&gt;    if (!class_exists(&#39;RarArchive&#39;)) {&lt;br&gt;      $GLOBALS[&#39;status&#39;] = array(&#39;error&#39; =&amp;gt; &#39;Error: Your PHP version does not support .rar archive functionality. &amp;lt;a class=&#34;info&#34; href=&#34;http://php.net/manual/en/rar.installation.php&#34; target=&#34;_blank&#34;&amp;gt;How to install RarArchive&amp;lt;/a&amp;gt;&#39;);&lt;br&gt;      return;&lt;br&gt;    }&lt;br&gt;    // Check if archive is readable.&lt;br&gt;    if ($rar = RarArchive::open($archive)) {&lt;br&gt;      // Check if destination is writable&lt;br&gt;      if (is_writeable($destination . &#39;/&#39;)) {&lt;br&gt;        $entries = $rar-&amp;gt;getEntries();&lt;br&gt;        foreach ($entries as $entry) {&lt;br&gt;          $entry-&amp;gt;extract($destination);&lt;br&gt;        }&lt;br&gt;        $rar-&amp;gt;close();&lt;br&gt;        $GLOBALS[&#39;status&#39;] = array(&#39;success&#39; =&amp;gt; &#39;Files extracted successfully.&#39;);&lt;br&gt;      }&lt;br&gt;      else {&lt;br&gt;        $GLOBALS[&#39;status&#39;] = array(&#39;error&#39; =&amp;gt; &#39;Error: Directory not writeable by webserver.&#39;);&lt;br&gt;      }&lt;br&gt;    }&lt;br&gt;    else {&lt;br&gt;      $GLOBALS[&#39;status&#39;] = array(&#39;error&#39; =&amp;gt; &#39;Error: Cannot read .rar archive.&#39;);&lt;br&gt;    }&lt;br&gt;  }&lt;br&gt;&lt;br&gt;}&lt;br&gt;&lt;br&gt;/**&lt;br&gt; * Class Zipper&lt;br&gt; *&lt;br&gt; * Copied and slightly modified from http://at2.php.net/manual/en/class.ziparchive.php#110719&lt;br&gt; * @author umbalaconmeogia&lt;br&gt; */&lt;br&gt;class Zipper {&lt;br&gt;  /**&lt;br&gt;   * Add files and sub-directories in a folder to zip file.&lt;br&gt;   *&lt;br&gt;   * @param string $folder&lt;br&gt;   *   Path to folder that should be zipped.&lt;br&gt;   *&lt;br&gt;   * @param ZipArchive $zipFile&lt;br&gt;   *   Zipfile where files end up.&lt;br&gt;   *&lt;br&gt;   * @param int $exclusiveLength&lt;br&gt;   *   Number of text to be exclusived from the file path.&lt;br&gt;   */&lt;br&gt;  private static function folderToZip($folder, &amp;amp;$zipFile, $exclusiveLength) {&lt;br&gt;    $handle = opendir($folder);&lt;br&gt;&lt;br&gt;    while (FALSE !== $f = readdir($handle)) {&lt;br&gt;      // Check for local/parent path or zipping file itself and skip.&lt;br&gt;      if ($f != &#39;.&#39; &amp;amp;&amp;amp; $f != &#39;..&#39; &amp;amp;&amp;amp; $f != basename(__FILE__)) {&lt;br&gt;        $filePath = &#34;$folder/$f&#34;;&lt;br&gt;        // Remove prefix from file path before add to zip.&lt;br&gt;        $localPath = substr($filePath, $exclusiveLength);&lt;br&gt;&lt;br&gt;        if (is_file($filePath)) {&lt;br&gt;          $zipFile-&amp;gt;addFile($filePath, $localPath);&lt;br&gt;        }&lt;br&gt;        elseif (is_dir($filePath)) {&lt;br&gt;          // Add sub-directory.&lt;br&gt;          $zipFile-&amp;gt;addEmptyDir($localPath);&lt;br&gt;          self::folderToZip($filePath, $zipFile, $exclusiveLength);&lt;br&gt;        }&lt;br&gt;      }&lt;br&gt;    }&lt;br&gt;    closedir($handle);&lt;br&gt;  }&lt;br&gt;&lt;br&gt;  /**&lt;br&gt;   * Zip a folder (including itself).&lt;br&gt;   *&lt;br&gt;   * Usage:&lt;br&gt;   *   Zipper::zipDir(&#39;path/to/sourceDir&#39;, &#39;path/to/out.zip&#39;);&lt;br&gt;   *&lt;br&gt;   * @param string $sourcePath&lt;br&gt;   *   Relative path of directory to be zipped.&lt;br&gt;   *&lt;br&gt;   * @param string $outZipPath&lt;br&gt;   *   Relative path of the resulting output zip file.&lt;br&gt;   */&lt;br&gt;  public static function zipDir($sourcePath, $outZipPath) {&lt;br&gt;    $pathInfo = pathinfo($sourcePath);&lt;br&gt;    $parentPath = $pathInfo[&#39;dirname&#39;];&lt;br&gt;    $dirName = $pathInfo[&#39;basename&#39;];&lt;br&gt;&lt;br&gt;    $z = new ZipArchive();&lt;br&gt;    $z-&amp;gt;open($outZipPath, ZipArchive::CREATE);&lt;br&gt;    $z-&amp;gt;addEmptyDir($dirName);&lt;br&gt;    if ($sourcePath == $dirName) {&lt;br&gt;      self::folderToZip($sourcePath, $z, 0);&lt;br&gt;    }&lt;br&gt;    else {&lt;br&gt;      self::folderToZip($sourcePath, $z, strlen(&#34;$parentPath/&#34;));&lt;br&gt;    }&lt;br&gt;    $z-&amp;gt;close();&lt;br&gt;&lt;br&gt;    $GLOBALS[&#39;status&#39;] = array(&#39;success&#39; =&amp;gt; &#39;Successfully created archive &#39; . $outZipPath);&lt;br&gt;  }&lt;br&gt;}&lt;br&gt;?&amp;gt;&lt;br&gt;&lt;br&gt;&amp;lt;!DOCTYPE html&amp;gt;&lt;br&gt;&amp;lt;html&amp;gt;&lt;br&gt;&amp;lt;head&amp;gt;&lt;br&gt;  &amp;lt;title&amp;gt;File Unzipper + Zipper&amp;lt;/title&amp;gt;&lt;br&gt;  &amp;lt;meta http-equiv=&#34;Content-Type&#34; content=&#34;text/html; charset=UTF-8&#34;&amp;gt;&lt;br&gt;  &amp;lt;style type=&#34;text/css&#34;&amp;gt;&lt;br&gt;    &amp;lt;!--&lt;br&gt;    body {&lt;br&gt;      font-family: Arial, sans-serif;&lt;br&gt;      line-height: 150%;&lt;br&gt;    }&lt;br&gt;&lt;br&gt;    label {&lt;br&gt;      display: block;&lt;br&gt;      margin-top: 20px;&lt;br&gt;    }&lt;br&gt;&lt;br&gt;    fieldset {&lt;br&gt;      border: 0;&lt;br&gt;      background-color: #EEE;&lt;br&gt;      margin: 10px 0 10px 0;&lt;br&gt;    }&lt;br&gt;&lt;br&gt;    .select {&lt;br&gt;      padding: 5px;&lt;br&gt;      font-size: 110%;&lt;br&gt;    }&lt;br&gt;&lt;br&gt;    .status {&lt;br&gt;      margin: 0;&lt;br&gt;      margin-bottom: 20px;&lt;br&gt;      padding: 10px;&lt;br&gt;      font-size: 80%;&lt;br&gt;      background: #EEE;&lt;br&gt;      border: 1px dotted #DDD;&lt;br&gt;    }&lt;br&gt;&lt;br&gt;    .status--ERROR {&lt;br&gt;      background-color: red;&lt;br&gt;      color: white;&lt;br&gt;      font-size: 120%;&lt;br&gt;    }&lt;br&gt;&lt;br&gt;    .status--SUCCESS {&lt;br&gt;      background-color: green;&lt;br&gt;      font-weight: bold;&lt;br&gt;      color: white;&lt;br&gt;      font-size: 120%&lt;br&gt;    }&lt;br&gt;&lt;br&gt;    .small {&lt;br&gt;      font-size: 0.7rem;&lt;br&gt;      font-weight: normal;&lt;br&gt;    }&lt;br&gt;&lt;br&gt;    .version {&lt;br&gt;      font-size: 80%;&lt;br&gt;    }&lt;br&gt;&lt;br&gt;    .form-field {&lt;br&gt;      border: 1px solid #AAA;&lt;br&gt;      padding: 8px;&lt;br&gt;      width: 280px;&lt;br&gt;    }&lt;br&gt;&lt;br&gt;    .info {&lt;br&gt;      margin-top: 0;&lt;br&gt;      font-size: 80%;&lt;br&gt;      color: #777;&lt;br&gt;    }&lt;br&gt;&lt;br&gt;    .submit {&lt;br&gt;      background-color: #378de5;&lt;br&gt;      border: 0;&lt;br&gt;      color: #ffffff;&lt;br&gt;      font-size: 15px;&lt;br&gt;      padding: 10px 24px;&lt;br&gt;      margin: 20px 0 20px 0;&lt;br&gt;      text-decoration: none;&lt;br&gt;    }&lt;br&gt;&lt;br&gt;    .submit:hover {&lt;br&gt;      background-color: #2c6db2;&lt;br&gt;      cursor: pointer;&lt;br&gt;    }&lt;br&gt;    --&amp;gt;&lt;br&gt;  &amp;lt;/style&amp;gt;&lt;br&gt;&amp;lt;/head&amp;gt;&lt;br&gt;&amp;lt;body&amp;gt;&lt;br&gt;&amp;lt;p class=&#34;status status--&amp;lt;?php echo strtoupper(key($GLOBALS[&#39;status&#39;])); ?&amp;gt;&#34;&amp;gt;&lt;br&gt;  Status: &amp;lt;?php echo reset($GLOBALS[&#39;status&#39;]); ?&amp;gt;&amp;lt;br/&amp;gt;&lt;br&gt;  &amp;lt;span class=&#34;small&#34;&amp;gt;Processing Time: &amp;lt;?php echo $time; ?&amp;gt; seconds&amp;lt;/span&amp;gt;&lt;br&gt;&amp;lt;/p&amp;gt;&lt;br&gt;&amp;lt;form action=&#34;&#34; method=&#34;POST&#34;&amp;gt;&lt;br&gt;  &amp;lt;fieldset&amp;gt;&lt;br&gt;    &amp;lt;h1&amp;gt;Archive Unzipper&amp;lt;/h1&amp;gt;&lt;br&gt;    &amp;lt;label for=&#34;zipfile&#34;&amp;gt;Select .zip or .rar archive or .gz file you want to extract:&amp;lt;/label&amp;gt;&lt;br&gt;    &amp;lt;select name=&#34;zipfile&#34; size=&#34;1&#34; class=&#34;select&#34;&amp;gt;&lt;br&gt;      &amp;lt;?php foreach ($unzipper-&amp;gt;zipfiles as $zip) {&lt;br&gt;        echo &#34;&amp;lt;option&amp;gt;$zip&amp;lt;/option&amp;gt;&#34;;&lt;br&gt;      }&lt;br&gt;      ?&amp;gt;&lt;br&gt;    &amp;lt;/select&amp;gt;&lt;br&gt;    &amp;lt;label for=&#34;extpath&#34;&amp;gt;Extraction path (optional):&amp;lt;/label&amp;gt;&lt;br&gt;    &amp;lt;input type=&#34;text&#34; name=&#34;extpath&#34; class=&#34;form-field&#34; /&amp;gt;&lt;br&gt;    &amp;lt;p class=&#34;info&#34;&amp;gt;Enter extraction path without leading or trailing slashes (e.g. &#34;mypath&#34;). If left empty current directory will be used.&amp;lt;/p&amp;gt;&lt;br&gt;    &amp;lt;input type=&#34;submit&#34; name=&#34;dounzip&#34; class=&#34;submit&#34; value=&#34;Unzip Archive&#34;/&amp;gt;&lt;br&gt;  &amp;lt;/fieldset&amp;gt;&lt;br&gt;&lt;br&gt;  &amp;lt;fieldset&amp;gt;&lt;br&gt;    &amp;lt;h1&amp;gt;Archive Zipper&amp;lt;/h1&amp;gt;&lt;br&gt;    &amp;lt;label for=&#34;zippath&#34;&amp;gt;Path that should be zipped (optional):&amp;lt;/label&amp;gt;&lt;br&gt;    &amp;lt;input type=&#34;text&#34; name=&#34;zippath&#34; class=&#34;form-field&#34; /&amp;gt;&lt;br&gt;    &amp;lt;p class=&#34;info&#34;&amp;gt;Enter path to be zipped without leading or trailing slashes (e.g. &#34;zippath&#34;). If left empty current directory will be used.&amp;lt;/p&amp;gt;&lt;br&gt;    &amp;lt;input type=&#34;submit&#34; name=&#34;dozip&#34; class=&#34;submit&#34; value=&#34;Zip Archive&#34;/&amp;gt;&lt;br&gt;  &amp;lt;/fieldset&amp;gt;&lt;br&gt;&amp;lt;/form&amp;gt;&lt;br&gt;&amp;lt;p class=&#34;version&#34;&amp;gt;Unzipper version: &amp;lt;?php echo VERSION; ?&amp;gt;&amp;lt;/p&amp;gt;&lt;br&gt;&amp;lt;/body&amp;gt;&lt;br&gt;&amp;lt;/html&amp;gt;&lt;br&gt;&lt;/pre&gt;&lt;div id=&#34;hexo-adsense-hidden&#34; style=&#34;display:none&#34;&gt;&lt;div hexo-adsense=&#34;ads-content&#34;&gt;
